sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var location = request.pathParams.location;
    var utilities = new MyUtilities();
    try {
        var weatherData = utilities.getWeatherData(location);
        response.setStatus(200);
        response.setContentType('application/json');
        response.getStreamWriter().writeString(JSON.stringify(weatherData));
    } catch (ex) {
        // Handle errors (e.g., no data found or API errors)
        response.setStatus(ex.message === "No location results found or error fetching weather data." ? 404 : 500);
        response.setContentType('application/json');
        response.getStreamWriter().writeString(JSON.stringify({error: ex.message}));
    }
})(request, response);
