sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var location = request.pathParams.location; // Extrahiere den Standort aus dem Pfad

    var restMessage = new sn_ws.RESTMessageV2(); // ServiceNow verwendet RESTMessageV2-Instanz zur Kommunikation

    // Setze die HTTP-Methode und die Endpoint-URL
    restMessage.setHttpMethod('GET');
    restMessage.setEndpoint('https://geocoding-api.open-meteo.com/v1/search?name=' + encodeURIComponent(location) + '&limit=1');
    try {
        var restResponse = restMessage.execute();
        var responseBody = restResponse.getBody();
        var jsonData = JSON.parse(responseBody);

        if (jsonData.results && jsonData.results.length > 0) {
            var firstResult = jsonData.results[0];

            // Bereite die Daten für die Rückgabe vor
            var result = {
                id: firstResult.id,
                name: firstResult.name,
                latitude: firstResult.latitude,
                longitude: firstResult.longitude
            };

            response.setStatus(200); // Setze den HTTP-Statuscode korrekt
            response.setContentType('application/json');
            //response.setBody(JSON.stringify(result)); // Setze den Antwortkörper
			response.getStreamWriter().writeString(JSON.stringify(result));
        } else {
            // Keine Ergebnisse gefunden
            response.setStatus(404); // Not Found
            response.setContentType('application/json');
			response.getStreamWriter().writeString(JSON.stringify({error: "No results found"}));
        }
    } catch (ex) {
        // Fehlerbehandlung
        response.setStatus(500); // Internal Server Error
        response.setContentType('application/json');
        response.getStreamWriter().writeString(JSON.stringify({error: ex.message}));
    }

})(request, response);
